svg.script:
var e = function( id ) { return document.getElementById( id ); };
var svgFontsSupported =
  e("font_i").getBBox().width == e("path_i").getBBox().width &&
  e("font_M").getBBox().width == e("path_M").getBBox().width &&
  e("font_i").getBBox().width > e("font_M").getBBox().width;

window.addEventListener("message", function ( e ) {
  if ( e.source !== window ) return;
  console.log(e)
  alert( "result = " + JSON.stringify( e.data, null, 2 ) +
         "\n\n(Would be postMessaged to parent if framed.)" );
} );
window.parent.postMessage( { svgFontsSupported: svgFontsSupported }, "*" );
